import React, { useState } from "react";
import {Redirect,NavLink} from 'react-router-dom';

const Login = ()=> {

    const [user,setState] = useState({
        username : "",
        password : "",
        loggedIn : false
    });

    const handleChange = (e) => {


        const {name,value} = e.target;

        setState((preValue) => {
            //console.log(preValue);
            return {
                ...preValue,
                [name] : value
            };
        });
    }

    const onsubmit = (e) => {
        e.preventDefault();

        if(user.username==="shub" && user.password==="123"){
            setState({
                loggedIn : true
            });
        }
    };


    return (
        <>
            <div className="container-fluid nav-bg">
        <div className="row">
          <div className="col-12 mx-auto">

            <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
              <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
              </button>

              <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav ml-auto">
                  <li className="nav-item active">
                    <NavLink exact activeClassName="menu-active" className="nav-link" to="/sign-in">Login <span className="sr-only">(current)</span></NavLink>
                  </li>
                  <li className="nav-item active">
                    <NavLink exact activeClassName="menu-active" className="nav-link" to="/sign-up">Sign up <span className="sr-only">(current)</span></NavLink>
                  </li>

                </ul>

              </div>
            </nav>
          </div>
        </div>
      </div>




        <div className="d-flex justify-content-center mt-5">
            <form onSubmit={onsubmit}>
                <h3>Sign In</h3>

                <div className="form-group">
                    <label>Username</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        placeholder="Enter username" 
                        name = "username"
                        onChange = {handleChange}
                        value = {user.username}
                        required/>       
                </div>

                <div className ="form-group">
                    <label>Password</label>
                    <input 
                        type="password" 
                        className="form-control" 
                        placeholder="Enter password" 
                        name = "password"
                        onChange = {handleChange}
                        value = {user.password}
                        required/>
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input 
                            type="checkbox" 
                            className="custom-control-input" 
                            id="customCheck1" 
                            name = "remember-me"

                            />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>
                <div>
                <button type="submit" className="btn btn-primary btn-block" onClick={onsubmit}>Submit</button>
                {(user.loggedIn)?<Redirect to="/home"/>:<Redirect to ="/sign-in"/>}
                </div>
                <p className="forgot-password text-center mt-2">
                    <a href="#"><u>Forgot password?</u></a>
                </p>
            </form>
        </div>
        </>
    );

}

export default Login;
